import React from 'react'
import Slider from 'react-slick'
// import { Image } from 'antd';
const SliderComponent = ({arrImg}) => {
   const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1
      };
   return (
      <Slider settings={settings}>
         {arrImg.map((img) => {
            return (
               <img src={img} />
            )
         })}
      </Slider>
   )
}

export default SliderComponent
