import React, {useState} from "react";
import { Col } from "antd";
import {
   WrapperHeader,
   LogoText,
   NavbarWrapper,
   NavItem,
   SubnavItem,
   Dropdown,
   Subnav,
} from "./style";

import { useLocation } from 'react-router-dom';

const HeaderComponent = () => {
   const location = useLocation();
   const [currentPage, setCurrentPage] = useState('Home');

  // Set trang hiện tại dựa trên địa chỉ URL
   React.useEffect(() => {
      const pathname = location.pathname.substr(1);
      setCurrentPage(pathname || 'Home');
   }, [location]);

return (
   <div>
      <WrapperHeader>
         <Col span={4}>
            <LogoText>Yen Scented Candle</LogoText>
         </Col>
         <Col span={14}>
            <NavbarWrapper>
            <NavItem to="/" className={currentPage === 'Home' ? 'active' : ''}>Home</NavItem>
            <Dropdown>
               <NavItem to="/order" className={currentPage === 'Order' ? 'active' : ''}>Shop</NavItem>
               <Subnav className="subnav">
                  <SubnavItem>Collection 1</SubnavItem>
                  <SubnavItem>Collection 2</SubnavItem>
                  <SubnavItem>Collection 3</SubnavItem>
               </Subnav>
            </Dropdown>
            <NavItem to="/product" className={currentPage === 'Product' ? 'active' : ''}>Blog</NavItem>
            <NavItem to="/product" className={currentPage === 'Product' ? 'active' : ''}>About Us</NavItem>
            <NavItem to="/product" className={currentPage === 'Product' ? 'active' : ''}>Account</NavItem>
            </NavbarWrapper>
         </Col>
         <Col span={2}>col-8</Col>
      </WrapperHeader>
   </div>
);
};

export default HeaderComponent;
