import { Row } from "antd";
import styled from 'styled-components'

export const WrapperHeader = styled(Row)`
   width: 100vw;
   padding: 10px 50px;
   background: #ccc;
   display: flex;
   align-items: center;
   justify-content: space-between;
   flex-wrap: nowrap;
`

export const LogoText = styled.span`
   font-weight: bold;
   font-size: 1rem ;
   color: white;
   text-align: center
`

export const WrapperAccount = styled.div`
   display:flex;
   
   gap:10px
`
