import React, {useState} from "react";
import { Col } from "antd";
import {
   WrapperHeader,
   LogoText,
   NavbarWrapper,
   NavItem,
   SubnavItem,
   Dropdown,
   Subnav,
} from "./style";

import { Link, useLocation } from 'react-router-dom';

const HeaderComponent = () => {
   const [currentPage, setCurrentPage] = useState('Home');
   const handleClick = (page) => {
      setCurrentPage = page;
   };

return (
   <div>
      <WrapperHeader>
         <Col span={4}>
            <img src="" alt="" />
            <LogoText>Yen Scented Candle</LogoText>
         </Col>
         <Col span={14}>
            <NavbarWrapper>
            <NavItem style={currentPage=== 'Home'? {color:'#00B207'}: {}} onclick={() => handleClick('Home')}>Home</NavItem>
            <Dropdown>
               <NavItem style={currentPage=== 'Shop'? {color:'#00B207'}: {}} onclick={() => handleClick('Shop')}>Shop</NavItem>
               <Subnav className="subnav">
                  <SubnavItem>Collection 1</SubnavItem>
                  <SubnavItem>Collection 2</SubnavItem>
                  <SubnavItem>Collection 3</SubnavItem>
               </Subnav>
            </Dropdown>
            <NavItem style={currentPage=== 'Blog'? {color:'#00B207'}: {}} onclick={() => handleClick('Blog')}>Blog</NavItem>
            <NavItem style={currentPage=== 'About'? {color:'#00B207'}: {}} onclick={() => handleClick('About')}>About Us</NavItem>
            <NavItem style={currentPage=== 'Account'? {color:'#00B207'}: {}} onclick={() => handleClick('Account')}>Account</NavItem>
            </NavbarWrapper>
         </Col>
         <Col span={2}>col-8</Col>
      </WrapperHeader>
   </div>
);
};

export default HeaderComponent;
